var map;
var infowindow;
var request;
var service;
var markers = [];


function initialize(lat, lng) {
    var center = new google.maps.LatLng(lat, lng);
    // console.log(center);
    // console.log(lat);
    // console.log(lng);
    map = new google.maps.Map(document.getElementById('map'), {
        center: center,
        zoom: 12
    });

    var request = {
        location: center,
        radius: 8047,
        type: ['lodging']

    };

    infowindow = new google.maps.InfoWindow();
    var service = new google.maps.places.PlacesService(map);
    service.nearbySearch(request, callback);
}

function callback(returns, status) {
    if (status == google.maps.places.PlacesServiceStatus.OK) {
        for (var i = 0; i < returns.length; i++) {
            createMarker(returns[i]);
        }
    }
}

function createMarker(place) {


    // console.log(place);
    var placeLoc = place.geometry.location;

    // infowindow;
    // var infowindow = new google.maps.InfoWindow();
    var service = new google.maps.places.PlacesService(map);

    // google.maps.event.addListener(marker, 'click', function() {
    //    infowindow.setContent('<div><strong>' + place.name + '</strong><br>' +
    //            '<br>' +
    //            place.formatted_address + '<br>' + '</div>');

    //          infowindow.open(map, this);

    //        });

    service.getDetails({
        placeId: place.place_id
    }, function(place, status) {

        var marker = new google.maps.Marker({
            map: map,
            position: place.geometry.location,
            title: "Click here for more lit details"
        });

        google.maps.event.addListener(marker, 'click', function() {
            infowindow.setContent("<div><strong>" + place.name + "</strong><br> Address: " +
                place.formatted_address + "<br> Phone: " + place.formatted_phone_number +
                "<br> Rating: " + place.rating + "<br><div><a href='" + place.url + "' target='_blank'>Check Availability</div></div>");
            infowindow.open(map, this);

        });

    });


}

